/* efecto para las letras PDH por la defensa de los derechos humanos*/
#pplddldh-61a3e0f2eb189c6161463{
  overflow: hidden;
  white-space: nowrap;
  animation: typing 4s steps(42) 1s 1 normal both, blink 1s steps(1) infinite;
  animation-iteration-count: infinite;
}

/* efecto para las letras PDH por la defensa de los derechos humanos va escribiendo las letras*/
@keyframes typing {
  from {
    width: 0;
  }

  to {
    width: 100%;
  }
}

@keyframes blink {
  50% {
    border-color: transparent;
  }
}

/* Estilos para Servicios Denuncnias, Constancia y info publica */
.efectoDenun-Cons-Info:hover {
-webkit-transform: scale(1.1);
  transform: scale(1.1);
  overflow: hidden;
  transition: all .4s ease-in-out;
}
.efectoservicioimg:hover {
